CREATE OR REPLACE FORCE VIEW "EDO"."EDO_CANDIDATEDOSSIER_V" ("FIRST_NAME", "LAST_NAME", "USERNAME", "CANDIDACY_CAMPUS", "CANDIDACY_SCHOOL", "CANDIDACY_YEAR", "CANDIDATE_ID", "CURRENT_RANK", "PRIMARY_DEPARTMENT_ID", "SECONDARY_UNIT", "DOSSIER_ID", "DOSSIER_STATUS", "RANK_SOUGHT", "DUE_DATE", "DOCUMENT_ID", "WORKFLOW_ID")
AS
SELECT c.first_name,
c.last_name,
c.username,
c.candidacy_campus,
c.candidacy_school,
d.candidacy_year,
c.candidate_id,
c.current_rank,
c.primary_department_id,
d.secondary_unit,
d.dossier_id,
d.dossier_status,
d.rank_sought,
d.due_date,
d.document_id,
d.workflow_id
FROM edo_candidate_t c
INNER JOIN edo_dossier_t d
ON c.username = d.candidate_username;

CREATE TRIGGER "EDO_CANDIDATE_TR"
   before insert on "EDO_CANDIDATE_T"
   for each row
begin
   if inserting then
      if :NEW."CANDIDATE_ID" is null then
         select EDO_CANDIDATE_ID_S.nextval into :NEW."CANDIDATE_ID" from dual;
      end if;
   end if;
end;
/
COMMIT;

CREATE SEQUENCE EDO.EDO_REVIEW_LAYER_DEF_ID_S
  INCREMENT BY 1
  START WITH 15
  MAXVALUE 9999999999999999999999999999
  NOMINVALUE
  NOCYCLE
  NOCACHE
  ORDER;
COMMIT;

CREATE TRIGGER "EDO_REVIEW_LAYER_DEF_TR"
before insert on "EDO_REVIEW_LAYER_DEF_T"
for each row
