      if :NEW."REVIEW_LAYER_DEF_ID" is null then
        select EDO_REVIEW_LAYER_DEF_ID_S.nextval into :NEW."REVIEW_LAYER_DEF_ID" from dual;
      end if;
    end if;
  end;
/
COMMIT;

